from codefortest.alternating_char import alternatingCharacters
import unittest
class alternatingCharactersTest(unittest.TestCase):
    def test1(self):
        text = 'AAAA'
        test = alternatingCharacters(text)
        self.assertEqual(test,3)
    
    def test2(self):
        text = 'Hello World!'
        test = alternatingCharacters(text)
        self.assertEqual(test,1)
    
    def test3(self):
        text = 'WXXMMIYTTT'
        test = alternatingCharacters(text)
        self.assertEqual(test,4)

    def test4(self):
        text = 'abccddeeffgghhiijjkkllmmnnooppqqrrssttuuvvwwxxyyzzz'
        test = alternatingCharacters(text)
        self.assertEqual(test,25)

    def tes5(self):
        text = ''
        test = alternatingCharacters(text)
        self.assertEqual(test,490)

    def test6(self):
        text = 'AABBABABBBBBABBABABBBBABBABABABBABBABBBBAAABBBBBBBBBBBABBBBBBBABBBBBBBBBBBBABBABBBBAABBBBBAAAABBBBBB'
        test = alternatingCharacters(text)
        self.assertEqual(test,62)
    
    def test7(self):
        text = ''
        test = alternatingCharacters(text)
        self.assertEqual(test,4930)

    def test8(self):
        text = 'BBBBBBBBBBABBBBBBBBBBBBBBBBBBBBBBBBBABBBBBBBABBBBBBABBABAABBBBBABBBBBABBBBBBBABBBBBABBBBBBBABBAABBAB'
        test = alternatingCharacters(text)
        self.assertEqual(test,73)

    def tes9(self):
        text = ''
        test = alternatingCharacters(text)
        self.assertEqual(test,659)
    
    def test10(self):
        text = 'CSSSSSSSSSSSCLLLLLLLLLLL'
        test = alternatingCharacters(text)
        self.assertEqual(test,20)

